import React from 'react';

const RoundImgBtnWDescription = ({ image, description = "Button", url = "#", size = 20 }) => (
  <div className="text-center">
    <button
      onClick={() => window.open(url, '_blank')}
      className="p-0 bg-transparent border-none focus:outline-none hover:opacity-80 transition-opacity"
      aria-label={description}
    >
      <img
        src={image}
        alt={description}
        className={`w-${size} h-${size} object-cover rounded-full`}
        style={{ width: `${size * 4}px`, height: `${size * 4}px` }} // Fallback for unsupported Tailwind sizes
      />
    </button>
    <p className="mt-2 text-sm">{description}</p>
  </div>
);

export default RoundImgBtnWDescription;
