import React from 'react';

const SkillsCards = ({ imageSrc, title, bulletPoints }) => {
  return (
    <div className="flex flex-col md:flex-row bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200 max-w-3xl mx-auto">

      {/* Image Section */}
      <div className="md:w-1/2">
        <img src={imageSrc} alt={title} className="w-full h-full object-cover" />
      </div>

      {/* Content Section */}
      <div className="p-6 md:w-1/2 bg-gray-50">
        <h3 className="text-2xl font-bold mb-4 text-gray-800">{title}</h3>
        <ul className="list-disc list-inside text-gray-700 space-y-2">
          {bulletPoints.map((point, index) => (
            <li key={index}>{point}</li>
          ))}
        </ul>
      </div>

    </div>
  );
};

export default SkillsCards;
