import React from 'react';

const FirstPage = () => (
  <div>
    {/* First Section: Three Items Distributed Across the Width */}
    <div className="flex justify-center items-center py-16">
      <div className="flex justify-between w-full max-w-4xl">
        {/* Left Button */}
        <div className="text-center">
          <button className="bg-blue-500 text-white px-4 py-2 rounded">Button 1</button>
          <p className="mt-2">Description 1</p>
        </div>

        {/* Title */}
        <h1 className="text-4xl font-bold flex items-center justify-center">Title</h1>

        {/* Right Button */}
        <div className="text-center">
          <button className="bg-green-500 text-white px-4 py-2 rounded">Button 2</button>
          <p className="mt-2">Description 2</p>
        </div>
      </div>
    </div>

    {/* Second Section: Centered Image */}
    <div className="flex justify-center items-center py-16">
      <img src="https://via.placeholder.com/400" alt="Placeholder" className="object-contain" />
    </div>

    {/* Third Section: Div with Left and Right Sections */}
    <div className="bg-gray-200 py-16 w-full flex justify-center">
      <div className="flex w-full max-w-6xl space-x-8">
        {/* Left Section: Image and Text */}
        <div className="w-1/2 space-y-4">
          <img src="https://via.placeholder.com/200" alt="Placeholder" className="w-full" />
          <p>Some descriptive text goes here.</p>
        </div>

        {/* Right Section: Stacked Buttons */}
        <div className="w-1/2 flex flex-col space-y-4 justify-center items-center">
          <button className="bg-red-500 text-white px-4 py-2 rounded w-1/2">Button A</button>
          <button className="bg-yellow-500 text-white px-4 py-2 rounded w-1/2">Button B</button>
          <button className="bg-purple-500 text-white px-4 py-2 rounded w-1/2">Button C</button>
        </div>
      </div>
    </div>
  </div>
);

export default FirstPage;
